Command = ""
Started = False
while True:
      Command = input("> ").lower()
      if Command == "start":
          if Started:
            print("Car is already started!")
          else:
            Started = True
            print("Car sarted...")
      elif Command == "stop":
            if not Started:
               print("Car is already stopped!")
            else:
               Started = False
               print("Car Stopped...")
      elif Command == "help":
            print("""
start - to start the car
stop - to stop the car 
quit - to exit """)
      elif Command == "quit":
         break
      else:
          print("Sorry, I don't understand that")
      
    
